import { useState, useCallback } from "react";

import Section from "../UI/Section";
import TaskForm from "./TaskForm";
import useHttp from "../../hooks/use-http";
const NewTask = (props) => {
  const [taskText, setTaskText] = useState();

  const taskHandler = (text) => {
    setTaskText(text);
  };

  const { isLoading, error, sendRequest: enterTaskHandler } = useHttp();

  const postHandler = useCallback((data) => {
    const generatedId = data.name; // firebase-specific => "name" contains generated id
    const createdTask = { id: generatedId, text: taskText };

    enterTaskHandler(
      {
        url: "https://react-post-data-53086-default-rtdb.firebaseio.com/tasks.json",
        method: "POST",
        body: { text: taskText },
        headers: { "Content-Type": "application/json" },
      },
      postHandler
    ),
      props.onAddTask(createdTask);
  }, []);

  return (
    <Section>
      <TaskForm onEnterTask={taskHandler} loading={isLoading} />
      {error && <p>{error}</p>}
    </Section>
  );
};

export default NewTask;
